mapper.select:
 SELECT board_idx, subject,
		name, date, bhit FROM board WHERE importance = #{importance} AND
		board_category = 1 ORDER BY date DESC, board_idx DESC LIMIT #{limit}
		OFFSET #{offset}

mapper.update:
 UPDATE board SET
		subject = #{subject}, content = #{content}, importance =
		#{importance}, `date` = NOW() WHERE board_idx = #{board_idx}

mapper.select:
 SELECT board_idx,
		subject, name, date, bhit FROM board WHERE board_category = 2 ORDER BY
		date DESC, board_idx DESC LIMIT #{cnt} OFFSET #{offset}

mapper.update:
 UPDATE board SET subject
		= #{subject}, content = #{content},`date` = NOW() WHERE board_idx =
		#{board_idx}

mapper.select:
SELECT emp_idx
FROM comment
WHERE comment_idx = #{parent_idx}
