mapper.select:
	SELECT CEIL(COUNT(doc_idx) / #{cnt}) AS pages
	FROM approv a
	WHERE emp_idx = #{emp_idx}
  	AND (
	      (status = '상신' AND approv_order = 1)
	      OR
	      (status = '상신' AND approv_order = 2
	          AND doc_idx IN (
	              SELECT doc_idx
	              FROM approv
	              WHERE status = '승인' AND approv_order = 1
	          )
	      )
  	)

	    AND a.doc_read = #{readStatus}

SELECT a.approv_num, a.status, a.doc_read, a.doc_idx, d.doc_number, d.doc_subject, d.form_idx, d.doc_date, f.form_subject, e.name FROM approv a JOIN document d ON d.doc_idx = a.doc_idx JOIN form f ON d.form_idx = f.form_idx JOIN employee e ON e.emp_idx = d.emp_idx WHERE a.emp_idx = #{emp_idx} AND a.status = '상신' AND ( a.approv_order = 1 OR (a.approv_order = 2 AND a.doc_idx IN ( SELECT doc_idx FROM approv WHERE status = '승인' AND approv_order = 1 ) ) )
AND a.doc_read = #{readStatus}
ORDER BY d.doc_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT d.doc_idx, d.doc_number, d.doc_subject, d.doc_date, d.status, e.name, f.form_subject, a1.approv_num, a1.doc_read FROM document d JOIN ( SELECT doc_idx, approv_date, approv_num, doc_read FROM approv a WHERE emp_idx = #{emp_idx} AND approv_order = 1 AND status = '승인' ) a1 ON d.doc_idx = a1.doc_idx JOIN employee e ON d.emp_idx = e.emp_idx JOIN form f ON d.form_idx = f.form_idx WHERE d.status = '진행중'
AND a1.doc_read = #{readStatus}
ORDER BY d.doc_date DESC LIMIT #{limit} OFFSET #{offset}

   		SELECT CEIL(COUNT(doc_idx) / #{cnt}) AS pages
		FROM document d
		WHERE d.doc_idx IN (
		    SELECT a.doc_idx
		    FROM approv a
		    WHERE a.emp_idx = #{emp_idx}
	      	AND a.status = '승인'

			    AND a.doc_read = #{readStatus}

		)
		AND d.status = '승인'

SELECT d.doc_idx, d.doc_number, d.doc_subject, d.doc_date, d.status, e.name, f.form_subject, a.approv_date, a.approv_num, a.doc_read FROM document d JOIN employee e ON d.emp_idx = e.emp_idx JOIN form f ON d.form_idx = f.form_idx JOIN approv a ON d.doc_idx = a.doc_idx WHERE a.emp_idx = #{emp_idx} AND a.status = '승인' AND d.status = '승인'
AND a.doc_read = #{readStatus}
ORDER BY d.doc_date DESC LIMIT #{limit} OFFSET #{offset}

		SELECT CEIL(COUNT(a.doc_idx) / #{cnt}) AS pages
		FROM approv a
		WHERE	
			a.doc_idx IN (
		        SELECT doc_idx 
		        FROM approv 
		        WHERE status = '반려'
		    )
   		AND a.emp_idx = #{emp_idx}

	    	AND a.doc_read = #{readStatus}

SELECT a.doc_idx, a.doc_read, a.approv_date, a.approv_num, d.status, d.doc_number, d.doc_subject, d.doc_date, f.form_subject, e.name FROM approv a JOIN document d ON a.doc_idx = d.doc_idx JOIN form f ON d.form_idx = f.form_idx JOIN employee e ON d.emp_idx = e.emp_idx WHERE a.doc_idx IN ( SELECT doc_idx FROM approv WHERE emp_idx = #{emp_idx} ) AND a.status = '반려' AND a.doc_idx IN ( SELECT doc_idx FROM approv WHERE status = '반려' )
AND a.doc_read = #{readStatus}
ORDER BY d.doc_date DESC LIMIT #{limit} OFFSET #{offset}

    	SELECT CEIL(COUNT(doc_idx) / #{cnt}) AS pages
    	FROM reference WHERE emp_idx = #{emp_idx}

	    	AND doc_read = #{readStatus}

SELECT d.doc_idx, d.doc_number, d.doc_subject, d.doc_date, d.status, e.name, f.form_subject, r.doc_read, r.ref_idx AS approv_num FROM document d JOIN employee e ON d.emp_idx = e.emp_idx JOIN form f ON d.form_idx = f.form_idx JOIN reference r ON d.doc_idx = r.doc_idx WHERE r.emp_idx = #{emp_idx}
AND r.doc_read = #{readStatus}
ORDER BY d.doc_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT d.doc_idx, d.doc_number, d.doc_subject, d.doc_date, d.status, e.name, f.form_subject, r.doc_read, r.ref_idx AS approv_num FROM document d JOIN employee e ON d.emp_idx = e.emp_idx JOIN form f ON d.form_idx = f.form_idx JOIN reference r ON d.doc_idx = r.doc_idx WHERE r.emp_idx = #{emp_idx}
AND d.doc_subject LIKE CONCAT('%', #{query}, '%')
AND e.name LIKE CONCAT('%', #{query}, '%')
AND d.doc_number LIKE CONCAT('%', #{query}, '%')
AND f.form_subject LIKE CONCAT('%', #{query}, '%')
ORDER BY d.doc_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT emp_idx
FROM reference
WHERE doc_idx = #{doc_idx}

SELECT doc_content, emp_idx
FROM document
WHERE doc_idx = #{doc_idx}
