mapper.select:
SELECT d.doc_idx, d.doc_number, d.doc_subject, d.doc_date, d.status, e.name, f.form_subject, r.doc_read, r.ref_idx FROM document d JOIN employee e ON d.emp_idx = e.emp_idx JOIN form f ON d.form_idx = f.form_idx JOIN reference r ON d.doc_idx = r.doc_idx WHERE r.emp_idx = #{emp_idx}
AND d.doc_subject LIKE CONCAT('%', #{query}, '%')
AND e.name LIKE CONCAT('%', #{query}, '%')
AND d.doc_number LIKE CONCAT('%', #{query}, '%')
ORDER BY d.doc_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT CEIL(COUNT(doc_idx) / #{cnt}) AS pages FROM document WHERE emp_idx = #{emp_idx} AND status = #{status}
AND doc_subject LIKE CONCAT('%', #{query}, '%')
AND doc_number LIKE CONCAT('%', #{query}, '%')

SELECT d.doc_idx, d.form_idx, d.doc_subject, d.doc_date, d.status, d.doc_number, f.form_subject FROM document d JOIN form f ON d.form_idx = f.form_idx WHERE d.emp_idx = #{emp_idx} AND status = #{status}
AND doc_subject LIKE CONCAT('%', #{query}, '%')
AND doc_number LIKE CONCAT('%', #{query}, '%')
ORDER BY d.doc_date DESC LIMIT #{limit} OFFSET #{offset}
